import { createAsyncThunk, createReducer } from '@reduxjs/toolkit'
import axios from "axios";

//*Trae las películas que vos ingresas en el buscador.

export const getMoviesRequest = createAsyncThunk("MOVIES", (input) => {
    return axios.get(`https://www.omdbapi.com/?apikey=20dac387&s=${input}&plot=short`)
    .then(objeto => objeto.data.Search)
});

//* Para que la landing page siempre traiga un listado de películas (random??).
/* 
export const getLanding = createAsyncThunk("LANDING", () => {
    return axios.get(`https://www.omdbapi.com/?apikey=20dac387&y=${2021}`)
    .then(objeto => objeto.data.Search)
}); */

export const moviesReducer = createReducer([], {
    [getMoviesRequest.fulfilled]: (state, action) => action.payload,
    /* [getLanding.fulfilled]: (state, action) => action.payload */
});
