import React from 'react';
import { useSelector } from 'react-redux'
import { Card, Button } from 'react-bootstrap';
// aca va a llegar el input, y va a mostrar la información espefcífica de esa película
export default () => {
    const singleMovie = useSelector(state => state.singleMovie)

    return (      

<Card style={{ width: '18rem' }}>
  <Card.Img variant="top" src={singleMovie.Poster} />
  <Card.Body>
    <Card.Title>{singleMovie.Title}</Card.Title>
    <Card.Text>
    {singleMovie.Plot}
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>

    )

}
