import React from 'react';
import { getMoviesRequest } from '../../state/movies'
import { useDispatch, useSelector } from 'react-redux'
import { getSingleMovie } from '../../state/singleMovie'
import { Link } from 'react-router-dom';

import Search from '../containers/Search'

// aca va a llegar el input, y va a mostrar la información espefcífica de esa película
export default () => {
    const movies = useSelector(state => state.movies)
    const dispatch = useDispatch()
    console.log(movies)

    const scrollToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: "smooth"
        });
    }

    const handleClick = (id) => {       
        dispatch(getSingleMovie(id))
        scrollToTop()
    }

    return (
        <>
            <div>{movies.length > 0 ? movies.map(p => {
                return <div key={p.imdbID} >

                    <Link
                        onClick={() => handleClick(p.imdbID)}
                        to={`/movie/:${p.imdbID}`}>

                        <h3> {p.Title} </h3>
                        <img src={p.Poster} width="110" height="130" />
                    </Link>

                </div>
            }) : null}</div>
        </>


    )

}
