import React from 'react';
import { useSelector } from 'react-redux'

// aca va a llegar el input, y va a mostrar la información espefcífica de esa película
export default () => {
    const singleMovie = useSelector(state => state.singleMovie)

    return (      
        <div className="col-xs-2">        
            <img src={singleMovie.Poster} />      
            <ul><h1>{singleMovie.Title}</h1></ul>
            <ul>{singleMovie.Plot}</ul>
            <ul>{singleMovie.Directors}</ul>
            <ul>{singleMovie.Actors}</ul>
            <ul>{singleMovie.Year}</ul>
            <ul>{singleMovie.Genre}</ul> 
        </div> 

    )

}
